import React from 'react';
import Container from 'react-bootstrap/Container';
// import Row from 'react-bootstrap/Row';
// import Col from 'react-bootstrap/Col';
import './pokecard.css';

const Pokecard = (props) => {
    // for(let prop of props.pokedex) {
    //     console.log(props.pokedex)
        return(
            <Container>
                <Row>{props.pokedex.map(p => (
        <div className="Pokecard">
		<h4 className="Pokecard-name">{p.name}</h4>
		<img className="Pokecard-img" src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${p.id}.png`} alt="" />
		<div>
        <p>Type: {p.type}</p>
        <p>XP: {p.base_experience}</p>
		</div>
        </div>
            ))}
                </Row>
        </Container>
        )
    // }
};

export default Pokecard;
