import React from 'react';
import Container from 'react-bootstrap/Container';
// import Row from 'react-bootstrap/Row';
// import Col from 'react-bootstrap/Col';
import './pokecard.css';

const Pokecard = (props) => {
    // for(let prop of props.pokedex) {
    //     console.log(props.pokedex)
        return(
            <Container>
                <Row>
        <div className="Pokecard">
		<h4 className="Pokecard-name">{prop.name}</h4>
		<img className="Pokecard-img" src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${prop.id}.png`} alt="" />
		<div>
        <p>Type: {prop.type}</p>
        <p>XP: {prop.base_experience}</p>
		</div>
        </div>
                </Row>
        </Container>
        )
    // }
};

export default Pokecard;
